// @flow

import React, { Component } from "react";
// importing the style from the external css file
import "./home.css";


// declaring the type of states used
type Props = {};
type State = {

};

class Home extends Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      freelancerList: [],
      searchType: 'all',
      searchValue: '',
    };

    //  binding all the necessary functions to perform state operations
    (this: any).handleSearchTypeChange = this.handleSearchTypeChange.bind(this);
    (this: any).handleSearchValueChange = this.handleSearchValueChange.bind(this);
  }
  componentDidMount() {
    fetch('https://reqres.in/api/users?page=1')
    .then(response => response.json())
    .then(data_list => this.setState(prevState=>({
      freelancerList: data_list.data,
      })));
      console.log('freelancerList', this.state.freelancerList);
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('freelancerList', this.state.freelancerList);
  }

  handleSearchTypeChange(e) {
    const { value } = e.target;
    console.log('kokokok', value);
    this.setState(prevState=>({
      searchType: value,
    }));
  }
  handleSearchValueChange(e) {
    const { value } = e.target;
    console.log('valueee', value);
    this.setState(prevState=>({
      searchValue: value,
    }));
  }

  render() {
    let freelancer_list = this.state.freelancerList;
    if(this.state.searchType !== 'all') {
      freelancer_list = this.state.freelancerList.filter((item) => {
        return (
          (item.first_name && item.first_name.toLowerCase().search(this.state.guestSearchKeyword.toLowerCase()) !== -1) ||
          (item.last_name && item.last_name.toLowerCase().search(this.state.guestSearchKeyword.toLowerCase()) !== -1) ||
          (item.contact && item.contact.search(this.state.guestSearchKeyword) !== -1) ||
          (item.room_number && item.room_number.search(this.state.guestSearchKeyword) !== -1)
        );
      });
    }
    return (
      <div>
        <div>
          <select onChange={this.handleSearchTypeChange}>
            <option value="all">all</option>
            <option value="email">email</option>
            <option value="first_name">first name</option>
            <option value="last_name">last name</option>
          </select>
          <input
            id="search"
            type="text"
            value={this.state.searchValue}
            onChange={this.handleSearchValueChange}
          />
        </div>
        {
          freelancer_list.map((data, index)=>(
            <div>
              {data.name}
            </div>
          ))
        }
      </div>
      );
  }
}

export default Home;
